<script>
    export default {
        data() {
            return {
                fps: 60,
                logo: {
                    position: {
                        x: 0,
                        y: 0,
                    },
                    speed: {
                        x: 0,
                        y: 0,
                    },
                },
                interval: undefined,
            }
        },
        mounted() {
            this.$refs.canvas.style.height = window.innerHeight + "px";
            this.$refs.canvas.style.width = window.innerWidth + "px";

            // Logo moving velocity variables
            this.logo.position.x = 10;
            this.logo.position.y = 10;
            this.logo.speed.x = 3;
            this.logo.speed.y = 3;

            this.interval = setInterval(() => { this.update() }, 1000 / this.fps);
        },
        beforeDestroy() {
            clearInterval(this.interval)
        },
        methods: {
            update() {
                if (this.logo.position.x + this.$refs.logo.clientWidth >= window.innerWidth || this.logo.position.x <= 0) {
                    this.logo.speed.x = -this.logo.speed.x;
                    this.$refs.logo.style.fill = randomColor();
                }
                if (this.logo.position.y + this.$refs.logo.clientHeight >= window.innerHeight || this.logo.position.y <= 0) {
                    this.logo.speed.y = -this.logo.speed.y;
                    this.$refs.logo.style.fill = randomColor();
                }

                this.logo.position.x += this.logo.speed.x;
                this.logo.position.y += this.logo.speed.y;

                this.$refs.logo.style.left = this.logo.position.x + "px";
                this.$refs.logo.style.top = this.logo.position.y + "px";
            }
        }
    }

    function randomColor() {
        let color = "#";
        color += Math.random().toString(16).slice(2, 8).toUpperCase();

        return color;
    }
</script>